  #include "fsl_tpm.h" /* include SDK header file for timer module */
#elif MCUC1_CONFIG_SDK_VERSION_USED == MCUC1_CONFIG_SDK_KINETIS_1_3
  #error "SDK V1.x not supported"
#else
  #error "This component only works with the Kinetis SDK!"
#endif

#define TMR1_SOURCE_CLOCK_FREQUENCY (CLOCK_GetFreq(kCLOCK_McgIrc48MClk)/4)
  /*!< Timer Clock Frequency */

void TPM2_IRQHandler(void) {
  /* Clear interrupt flag.*/
  TPM_ClearStatusFlags(TMR1_CONFIG_TIMER_NAME, kTPM_TimeOverflowFlag);
  TMR1_OnInterrupt();
}
