/*
** ===================================================================
**     Method      :  Timer1_Init (component SDK_Timer)
**     Description :
**         Driver initialization
**     Parameters  : None
**     Returns     : Nothing
** ===================================================================
*/
void Timer1_Init(void)
{
  tpm_config_t tpmInfo;

  /* Select the clock source for the TPM counter as MCGPLLCLK */
  CLOCK_SetTpmClock(1U);

  TPM_GetDefaultConfig(&tpmInfo);
  /* TPM clock divide by 4 */
  tpmInfo.prescale = kTPM_Prescale_Divide_4;

  /* Initialize TPM module */
  TPM_Init(Timer1_CONFIG_TIMER_NAME, &tpmInfo);

  Timer1_SetPeriodUS(Timer1_CONFIG_TIMER_INIT_PERIOD_US); /* set initial period */
#if Timer1_CONFIG_TIMER_INIT_ENABLE_OVERFLOW_IRQ
  Timer1_EnableOverflowIRQ(); /* enable timer overflow interrupt */
#else
  Timer1_DisableOverflowIRQ(); /* disable timer overflow interrupt */
#endif
#if Timer1_CONFIG_TIMER_INIT_ENABLE_IRQ
  Timer1_EnableIRQ(); /* enable timer interrupt in NVIC */
#else
  Timer1_DisableIRQ(); /* disable timer interrupt in NVIC */
#endif
  NVIC_SetPriority(Timer1_CONFIG_TIMER_IRQ, 0); /* set interrupt priority */
#if Timer1_CONFIG_TIMER_INIT_ENABLE_TIMER
  Timer1_Enable(); /* start timer */
#else
  Timer1_Disable(); /* do not start timer */
#endif
}

