uint8_t McuSharpMemoryDisplay_DisplayBuf[McuSharpMemoryDisplay_DISPLAY_HW_NOF_ROWS][((McuSharpMemoryDisplay_DISPLAY_HW_NOF_COLUMNS-1)/8)+1]; /* buffer for the display */

/* some aspects of the protocol are pretty timing sensitive... */
#define McuSharpMemoryDisplay_BIT_WRITECMD   (0x80)
#define McuSharpMemoryDisplay_BIT_VCOM       (0x40)
#define McuSharpMemoryDisplay_BIT_CLEAR      (0x20)
#define McuSharpMemoryDisplay_TOGGLE_VCOM    do { McuSharpMemoryDisplay_sharpmem_vcom = McuSharpMemoryDisplay_sharpmem_vcom ? 0x00 : McuSharpMemoryDisplay_BIT_VCOM; } while(0);

static uint8_t McuSharpMemoryDisplay_sharpmem_vcom;

static uint8_t McuSharpMemoryDisplay_RevertBits(uint8_t data) {
  uint8_t tmp, i;

  /* revert bits */
  tmp = 0;
  for(i=0;;i++) {
    if (data&1) {
      tmp |= 0x1;
    }
    if (i==7) {
      break;
    }
    tmp <<= 1;
    data >>= 1;
  }
  return tmp;
}
