/*
** ===================================================================
**     Method      :  Init (component SharpMemDisplay)
**
**     Description :
**         Display driver initialization
**     Parameters  : None
**     Returns     : Nothing
** ===================================================================
*/
void McuSharpMemoryDisplay_Init(void)
{
  /* LCD expects LSB first (Send MSB first: no)!
   * Data is valid on clock rising edge and shifted on clock falling edge
   * Clock Idle Polarity: Low
   */
  SCEpin1_ClrVal(); /* disable device */
  McuGenericSWSPI_SetShiftClockPolarity(0); /* 0: falling edge: data is sampled at rising edge and shifted at falling edge */
  McuGenericSWSPI_SetIdleClockPolarity(0); /* 0: low idle polarity */
  /* Set the vcom bit to a defined state */
  McuSharpMemoryDisplay_sharpmem_vcom = McuSharpMemoryDisplay_BIT_VCOM;
}

