/*
** ===================================================================
**     Method      :  UpdateLine (component SharpMemDisplay)
**
**     Description :
**         Updates a single line on the LCD
**     Parameters  :
**         NAME            - DESCRIPTION
**         line            - Line number to update, range 0-93
**       * dataP           - Pointer to data, must be array of 12
**                           bytes (96 pixels)
**     Returns     : Nothing
** ===================================================================
*/
void McuSharpMemoryDisplay_UpdateLine(uint8_t line, uint8_t *dataP)
{
  int i;

  /* Send the write command */
  SCEpin1_SetVal();
  WriteData(McuSharpMemoryDisplay_BIT_WRITECMD | McuSharpMemoryDisplay_sharpmem_vcom);
  McuSharpMemoryDisplay_TOGGLE_VCOM;

  /* Send the address for line */
  WriteData(McuSharpMemoryDisplay_RevertBits(line+1)); /* line starts with 1 */

  /* Send data byte for selected line */
  for (i=0; i<(McuSharpMemoryDisplay_HW_WIDTH/8); i++) {
    WriteData(*dataP);
    dataP++;
  }
  /* Send trailing 16 bits  */
  WriteData(0x00);
  WriteData(0x00);
  SCEpin1_ClrVal();
}

/*
** ===================================================================
**     Method      :  UpdateFull (component SharpMemDisplay)
**
**     Description :
**         Updates the whole display
**     Parameters  : None
**     Returns     : Nothing
** ===================================================================
*/
void McuSharpMemoryDisplay_UpdateFull(void)
{
  uint16_t numBytes = sizeof(McuSharpMemoryDisplay_DisplayBuf);
  int i, currentline, oldline;
  uint8_t *data = (uint8_t*)McuSharpMemoryDisplay_DisplayBuf;

  /* Send the write command */
  SCEpin1_SetVal();
  WriteData(McuSharpMemoryDisplay_BIT_WRITECMD | McuSharpMemoryDisplay_sharpmem_vcom);
  McuSharpMemoryDisplay_TOGGLE_VCOM;

  /* Send the address for line 1 */
  oldline = currentline = 1;
  WriteData(McuSharpMemoryDisplay_RevertBits(currentline));

  /* Send image buffer */
  for (i=0; i<numBytes; i++) {
    WriteData(*data);
    data++;
    currentline = ((i+1)/(McuSharpMemoryDisplay_HW_WIDTH/8)) + 1;
    if(currentline != oldline) {
      WriteData(0x00); /* send 8 traling bits to finish current line */
      if (currentline <= McuSharpMemoryDisplay_HW_HEIGHT) {
        /* send new line number */
        WriteData(McuSharpMemoryDisplay_RevertBits(currentline));
      }
      oldline = currentline;
    }
  }
  /* Send another trailing 8 bits for the last line */
  WriteData(0x00);
  SCEpin1_ClrVal();
}

